export function toggleMenu(){const e=document.querySelector(".nav-toggle"),t=document.querySelector(".main-nav");if(!e||!t)return;e.addEventListener("click",(()=>{t.classList.toggle("open"),e.classList.toggle("active");const n="true"===e.getAttribute("aria-expanded")||!1;e.setAttribute("aria-expanded",!n),document.body.classList.toggle("nav-open")}));document.querySelectorAll(".nav-list a").forEach((n=>{n.addEventListener("click",(()=>{t.classList.remove("open"),e.classList.remove("active"),document.body.classList.remove("nav-open")}))}))}